import React, { useEffect, useState } from "react";
import axios from 'axios'

const Product = ({_id, name, manufacturer,description,color,price,imageFileName,screen,processor,ram}) => {
   const [product, setproduct] = useState({})

   useEffect(() => {

     axios.get(`http://localhost:4000/api/products/${match.params._id}`)
         .then(res => {
         setproduct(res.data)

        })
         .catch(error => console.log(error))

    }, [product]);

  return (
    <div className="col-md-4 p-2" >
      <div className="card">
        <img variant="top" src={imageFileName} />
        <div className="card-header">
          <h5>Name: {name}</h5>
        </div>
        <div className="card-body">
          <p>{description}</p>
          <p> {manufacturer}$</p>
          <p> {price}$</p>
          <p> {color}$</p>
          <p> {screen}$</p>
          <p> {processor}$</p>
          <p> {ram}$</p>
        </div>
      </div>
    </div>
  );
};

export default Product;
